import React from 'react';

const Amount = (props) => {
    const amount = props.amount;
    console.log(amount);

     let totalAmount = 0;
    for(let i = 0; i<amount.length; i++) {
        const product = amount[i];
        totalAmount += product.amount
    }
    


    return (
        <div>
            <h2>Order Summery</h2>
            <p>Product Price : {totalAmount}</p>
        </div>
    );
};

export default Amount;
