import React,{useState} from 'react';
import fakeData from '../../fakeData';
import Product from '../Product/Product';
import Amount from '../Amount/Amount';
import '../Shop/Shop.css'

const Shop = () => {
    const fakeDataTwo = fakeData.slice(0,12);
    console.log(fakeDataTwo);
    const [amount, setAmount ] = useState([])

    const [product, setProduct] = useState(fakeDataTwo);

    const handleEnrollButton = (product) => {
        const newAmount = [...amount, product]
        setAmount(newAmount);
    }
    return (
        <div className="main-container">
            <div className="product-container">
            {
                product.map( pd => <Product product={pd}>
                    handleEnrollButton ={handleEnrollButton}
                </Product>)
            }
            </div>
            
               <div className="card-container"> 
                
               <Amount amount={amount}></Amount>
            s
            </div> 
           
        </div>
    );
};

export default Shop;
